import React from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate} from 'react-router-dom';
import { useState } from 'react';



function ResultPage() {

   const navigate = useNavigate()
   const handleUpload = () => {
        navigate('/')
    }

    const location = useLocation();
    const previousFileUrl = location.state?.vocalPreviousFileUrl;
    const fileUrl = location.state?.vocalFileUrl;
    const imageUrl = location.state?.imageUrl;
    const mixFileUrl = location.state?.mixFileUrl;
    const instFileUrl = location.state?.instFileUrl;

    //ボーカルの音ズレ処理
    const [vocalOffset, setVocalOffset] = useState(0); // ボーカルの再生ズレ（秒）
    const [audioCtx, setAudioCtx] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);

    const playWithOffset = async () => {
    if (isPlaying) return;

    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    setAudioCtx(ctx);

    const loadAudio = async (url) => {
      const res = await fetch(url);
      const arrayBuffer = await res.arrayBuffer();
      return await ctx.decodeAudioData(arrayBuffer);
    };

    const instBuffer = await loadAudio(`http://localhost:5000${instFileUrl}`);
    const vocalBuffer = await loadAudio(`http://localhost:5000${fileUrl}`);

    const instSource = ctx.createBufferSource();
    instSource.buffer = instBuffer;
    instSource.connect(ctx.destination);

    const vocalSource = ctx.createBufferSource();
    vocalSource.buffer = vocalBuffer;
    vocalSource.connect(ctx.destination);

    const offset = Math.max(0, vocalOffset);
    const delay = Math.max(0, -vocalOffset);

    const startTime = ctx.currentTime + 0.1;
    instSource.start(startTime);
    vocalSource.start(startTime + offset);

    setIsPlaying(true);

    instSource.onended = () => {
      setIsPlaying(false);
    };
  };

    console.log("処理前:", previousFileUrl)
    console.log("処理後:", fileUrl)

    return (
      <div>
        <h1>処理完了</h1>
        <label>処理前
          <audio src={`http://localhost:5000${previousFileUrl}`} controls/>
        </label>
        <label>処理後
          <audio src={`http://localhost:5000${fileUrl}`} controls/>   
        </label>
          <img
            src={`http://localhost:5000${imageUrl}`}
            alt="processed waveform"
            style={{ maxWidth: '100%', height: 'auto' }}
          />

        <label>MIX処理後
          <audio src={`http://localhost:5000${mixFileUrl}`} controls/>
        </label>

        <div>
          <label>ボーカルのズレ（秒）: {vocalOffset.toFixed(2)}</label>
          <input
            type="range"
            min={-1}
            max={1}
            step={0.01}
            value={vocalOffset}
            onChange={(e) => setVocalOffset(parseFloat(e.target.value))}
          />
          <button onClick={playWithOffset} disabled={isPlaying}>再生（inst + vocal）</button>
        </div>

        <button onClick={handleUpload}>アップロード画面へ</button>
      </div>
    );
  }
  
  export default ResultPage;
